<?phpclass Admin {    static function fetchPlayerList() {        $db = Database::connect();        $stmt = $db->prepare('SELECT * FROM users LIMIT 10');        $stmt->execute();        return $stmt->fetchAll();    }    static function searchPlayer($search) {        $db = Database::connect();        $stmt = $db->prepare('SELECT * FROM users WHERE username LIKE :username LIMIT 10');        $stmt->execute([            "username" => '%'.$search.'%'        ]);        return $stmt->fetchAll();    }    static function fetchGeo($ip) {        $data = json_decode(file_get_contents("http://api.ipstack.com/$ip?access_key=83e1c61a663ea32691fcfca77b80f01e"));        return "https://www.countryflags.io/".$data->country_code."/flat/32.png";    }    static function ban($username, $hwid, $ip, $reason) {        $db = Database::connect();        // check if already banned for any reason        if (User::isBanned($username) || User::isHWIDBanned($hwid)) {            return;        }        $add_ban = $db->prepare("INSERT INTO bans (username, hwid, ip, reason) VALUES (:username, :hwid, :ip, :reason);");        $add_ban->execute([            "username" => $username,            "hwid" => $hwid,            "ip" => $ip,            "reason" => $reason        ]);    }}