<?phpclass General {    static function getClientIp() {        $ipaddress = "";        if (getenv('HTTP_CLIENT_IP')) {            $ipaddress = getenv('HTTP_CLIENT_IP');        } else if(getenv('HTTP_X_FORWARDED_FOR')){            $ipaddress = getenv('HTTP_X_FORWARDED_FOR');        } else if(getenv('HTTP_X_FORWARDED')) {            $ipaddress = getenv('HTTP_X_FORWARDED');        } else if(getenv('HTTP_FORWARDED_FOR')) {            $ipaddress = getenv('HTTP_FORWARDED_FOR');        } else if(getenv('HTTP_FORWARDED')) {            $ipaddress = getenv('HTTP_FORWARDED');        } else if(getenv('REMOTE_ADDR')) {            $ipaddress = getenv('REMOTE_ADDR');        } else {            $ipaddress = 'UNKNOWN';        }        return $ipaddress;    }    static function time($datetime) {        $now = new DateTime;        $ago = new DateTime($datetime);        $diff = $now->diff($ago);        $diff->w = floor($diff->d / 7);        $diff->d -= $diff->w * 7;        $string = ['y' => 'year', 'm' => 'month', 'w' => 'week', 'd' => 'day', 'h' => 'hour', 'i' => 'minute', 's' => 'second'];        foreach ($string as $k => &$v) {            if ($diff->$k) {                $v = $diff->$k.' '.$v.($diff->$k > 1 ? 's' : '');            } else {                unset($string[$k]);            }        }        $string = array_slice($string, 0, 1);        return $string ? implode(', ', $string) . ' ago' : 'just now';    }    static function randomString($length) {        $characters = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';        $charactersLength = strlen($characters);        $randomString = '';        for ($i = 0; $i < $length; $i++) {            $randomString .= $characters[rand(0, $charactersLength - 1)];        }        return $randomString;    }}